mapper.sql:
SELECT SF.UUID, SF.CONTRACT_NO, SF.CONTRACT_BUYER, SF.USE_COMPANY, SF.COMPANY_NAME, SF.SERVE_FEE_CASH, SF.CONTRACT_LOAN_TOTAL, SF.SEND_COMPANY_DATE, SF.GET_CONFIRM_DATE, SF.PERSON_LIABLE, SF.FIRST_PAY_FEE, SF.FIRST_PAY_DATE, SF.IS_THREE_CONTRACT, SF.SECOND_PAY_FEE, SF.SECOND_PAY_DATE, SF.THIRD_PAY_FEE, SF.THIRD_PAY_DATE, SF.FOUR_PAY_FEE, SF.FOUR_PAY_DATE, SF.FIVE_PAY_FEE, SF.FIVE_PAY_DATE, SF.NOTE FROM HITACHI_SERVE_FEE SF WHERE SF.SHOW_YN = 'Y'
AND SF.UUID = #{uuid}
AND SF.CONTRACT_NO LIKE '%' #{contract_no} '%'
AND SF.CONTRACT_BUYER LIKE '%' #{contract_buyer} '%'
AND SF.USE_COMPANY LIKE '%' #{use_company} '%'
AND SF.PERSON_LIABLE LIKE '%' #{person_liable} '%'
AND SF.IS_THREE_CONTRACT LIKE '%' #{is_three_contract} '%'
AND SF.SEND_COMPANY_DATE BETWEEN #{send_start_date} AND #{send_end_date}
AND SF.GET_CONFIRM_DATE BETWEEN #{receive_start_date} AND #{receive_end_date}
ORDER BY SF.SEND_COMPANY_DATE DESC
